import axios from "axios";
import { tryCatchHandlr } from "../../shared/helpers";

export const submitHandlr = async (e, isLogin, authContxt, useHistory) => {
    const history = useHistory();

    e.preventDefault();
    const payload = {};

    for(const [i, el] of [...e.target].entries()){
        //to omit 'submit el' key value pair to payload
        if(i+1 === e.target.length) break;
        payload[el.name] = el.value;
    }

    const request = axios
        .post(`/users/${isLogin ? 'login': 'register'}`,
            payload,
            {headers: {
                "Content-Type": "application/json",
            }}, 
        );

    const [data, error] = await tryCatchHandlr(request);

    if(data) {
        console.log(data);
        authContxt.loginHandlr();
        history.replace('/');
        return;
    }    

    // TODO create a login error to show error message on welcome
    console.log(error);
};
