import React, { useState } from "react";
import { Input } from "../UI/Input";
import { Button } from "../UI/Button";
import axios from 'axios';

export const AuthForm = props => {
    const [isLogin, setIsLogin] = useState(true);

    function* submitHandlr(e){
        e.preventDefault();

        const x = yield(1);

        console.log(x);

        // try {
        //     const post = yield axios.post('http://localhost:5000/users/register',
        //         {
        //             'email': 'email@email.com',
        //             'password': 'password1'
        //         },    //body or payload
        //         {headers: { //headers
        //             "Content-Type": "application/json",
        //         }}, 
        //     );

        //     console.log(post);
        // }catch(err){    
        //     console.log(err);
        // }

    };

    return (
        <div className="AuthForm">
            <form onSubmit={(e) => submitHandlr(e)}>
                <Input
                name="email"
                label="Email"
                type="email"
                placeholder='Email'
                required
                />
                <Input
                name="password"
                label="Password"
                type="password"
                placeholder="Password"
                required/>
                <Input
                type='submit'
                value={isLogin ? 'Login' : 'Create User'}/>            
            </form>
            <div className="AuthForm__btn--containers">
                <div className="AuthForm__btn--text">
                    {!isLogin ? 'Already have an account?': 'Need to create a new account?'}
                </div>
                <Button
                    clicked={() => setIsLogin((prevState) => !prevState)}>
                    {!isLogin ? 'Login with Existing Account': 'Create a Free Account'}
                </Button>
            </div>
        </div>
    )
};
